import { describe, it, beforeEach, afterEach } from "mocha";
import { assert } from "chai";
import proxyquire from "proxyquire";
import sinon from "sinon";
import configureMockStore from "redux-mock-store";
import thunk from "redux-thunk";
import testState from "test/testState";
import { base58Encode } from "../../../src/utils/base-58";
describe(`modules/auth/actions/register.js`, () => {
  proxyquire.noPreserveCache();
  const middlewares = [thunk];
  const mockStore = configureMockStore(middlewares);
  const fakeAugurJS = {
    augur: {
      accounts: {
        account: { address: "0x812e463089332df61e96b8ce663a66e61aadecd3" }
      }
    }
  };
  const loadAccountDataStub = { loadAccountData: () => {} };
  const thisTestState = Object.assign({}, testState, { loginAccount: {} });
  const store = mockStore(thisTestState);
  const fakeCallback = sinon.stub();
  const loginID =
    "";
  fakeAugurJS.augur.accounts.register = (password, cb) =>
    cb({ address: "0x812e463089332df61e96b8ce663a66e61aadecd3", loginID });
  const loadAccountDataTestString = "loadAccountData() called.";
  sinon
    .stub(loadAccountDataStub, "loadAccountData")
    .callsFake(account => ({ type: loadAccountDataTestString }));
  const updateIsLoggedStub = {
    updateIsLogged: () => {}
  };
  sinon
    .stub(updateIsLoggedStub, "updateIsLogged")
    .callsFake(() => ({ type: "update-is-logged" }));
  const action = proxyquire("../../../src/modules/auth/actions/register", {
    "../../../services/augurjs": fakeAugurJS,
    "./update-is-logged": updateIsLoggedStub,
    "./load-account-data": loadAccountDataStub
  });
  beforeEach(() => {
    store.clearActions();
  });
  afterEach(() => {
    store.clearActions();
  });
  it(`should register a new account`, () => {
    const expectedOutput = [{ type: loadAccountDataTestString }];
    store.dispatch(action.register("password", fakeCallback));
    store.dispatch(
      action.setupAndFundNewAccount(
        "password",
        base58Encode(testState.loginAccount.loginID),
        fakeCallback
      )
    );
    assert(
      fakeCallback.calledTwice,
      `the callback wasn't triggered 2 times as expected`
    );
    assert.deepEqual(
      store.getActions(),
      expectedOutput,
      `Didn't create a new account as expected`
    );
  });
});
